	INDEX (language)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ;

DROP TABLE IF EXISTS /*$wgDBprefix*/langrid_services_config;
CREATE TABLE /*$wgDBprefix*/langrid_services_config (
	config_id int(11) NOT NULL,
	config_name varchar(255),
	config_value text,
	PRIMARY KEY (config_id)
